INSERT INTO `faq_comments` (`comment_id`, `comment_time`, `comment_text`, `comment_ip`, `comment_host`, `comment_client`, `comment_user_id`, `comment_nick`, `comment_email`, `comment_faq_id`, `comment_correct`, `comment_vote`, `comment_updated`) VALUES
(139, 0, 'Wir testen EtoA mit den jeweils aktuellen Versionen von Mozilla Firefox, Internet Explorer und Opera. Ohne Probleme läuft das Spiel ab Internet Explorer 7, Firefox 1.0 und Opera 9, bei älteren Versionen können wir nicht ausschliessen dass es browserbedingt zu Fehlern kommen kann.', '', '', '', 0, '', '', 8, 0, 0, 0),
(140, 0, 'Ja, JavaScript wird für viele grafische Effekte und Darstellungen im Spiel benötigt.\r\nJavaScript kannst du folgendermassen aktivieren (ist standardmässig bei den meisten Browsern bereits aktiviert):\r\n[b]Mozilla Firefox:[/b]\r\nExtras -> Einstellungen -> Inhalt -> JavaScript aktivieren\r\n[b]Internet Explorer[/b]\r\nExtras -> Internetoptionen -> Sicherheit -> "Internet" wählen -> Stufe anpassen -> "Active Scripting" auf aktiv setzen -> Ok', '', '', '', 0, '', '', 9, 0, 0, 0),
(141, 0, 'Lade die Seite neu. Schau dabei, dass du nur ein Loginfenster offen hast.\r\nSollt dies nichts gebracht haben, leere deinen Cache und versuch das gleiche nochmal.', '', '', '', 0, '', '', 7, 0, 0, 0),
(142, 0, 'Ja. Du kannst unter Einstellungen -> Design ein Design auswählen.', '', '', '', 0, '', '', 10, 0, 0, 0),
(143, 0, 'Ja. Unter Einstellungen -> Design kannst du den Pfad zu deinem eigenen Bildpaket angeben. Dieses Bildpaket kann entweder auf einem externen Webserver gehostet oder lokal auf deiner Festplatte gespeichert sein. Beachte, dass lokale Bildpakete aber nur funktionieren wenn du an deinem PC bist, wenn du z.B. anschliessend von einem öffentlichen Computer aus spielst kann es zu Fehlern in der Darstellung kommen.', '', '', '', 0, '', '', 11, 0, 0, 0),
(144, 0, '[b]Mozilla Firefox (Deutsch)[/b]\r\n\r\nKlicke auf Extras -> Einstellungen -> Datenschutz -> Private Daten -> Jetzt löschen -> "Cache" wählen -> OK\r\n\r\n[b]Mozilla Firefox (Englisch)[/b]\r\n\r\nKlicke auf Tools -> Clear Private Data -> Cache wählen und den Button "Clear Private Data Now" klicken\r\n\r\n[b]Internet Explorer (Windows, Deutsch)[/b]\r\n\r\nExtras -> Internetoptionen -> Löschen... -> Temporäre Internetdateien -> Jetzt löschen\r\n\r\n[b]Safari (MacOS, Englisch, Deutsch analog)[/b]\r\n\r\nIn der Schaltflaeche auf Safari -> Empty Cache klicken\r\n\r\n[b]Opera (Windows, Deutsch)[/b]\r\n\r\nExtras -> Internetspuren löschen -> individuelle Auswahl -> Cache leeren -> löschen\r\n\r\n[i]Falls du Aenderungen oder Erweiterungen zu obigen Informationen hast, melde dich per Mail unter faq(at)etoa.ch. Vielen Dank![/i]', '', '', '', 0, '', '', 12, 0, 0, 0),
(145, 0, 'Der Cache ist ein interner Speicher deines Browsers, in dem kürzlich aufgerufene Seiten und Bilder gespeichert werden, damit diese nicht immer neu geladen werden müssen. Bei sich ständig ändernden Internetseiten wie z.B. diesem Spiel kann sich diese Funktion aber auch negativ auswirken, so dass Änderungen an den Grafiken oder am Seitenlayout nicht sofort übernommen werden.', '', '', '', 0, '', '', 13, 0, 0, 0),
(146, 0, 'Die Galaxie ist unterteilt in Sektoren, welche wieder unterteilt sind in Zellen. Jede Zelle beinhaltet entweder ein Sonnensystem, ein Asteroidenfeld, ein Nebel, ein Wurmloch oder leerer Raum.\r\nObjekte werden in Koordinaten der Form 1/3 : 2/4 : 5 angegeben, wobei der erste Teil für den Sektor, der zweite Teil für die Zelle und der dritte Teil für den Planeten, falls vorhanden, steht. Die erste Zahl ist jeweils die horizontale, die zweite Zahl die vertikale Koordinate.', '', '', '', 0, '', '', 19, 0, 0, 0),
(147, 0, 'Hier eine kleine Anleitung wie man sich ein Grafikpaket runterlädt und installiert:\r\n\r\n1. Lade dir ein Paket deiner Wahl unter "Einstellungen -> Design" (Ganz unten die Links) runter.\r\n2. Entpacke die Datei (Sie ist im ZIP-Format)\r\n3. Speichere den entpackten Ordner irgendwo auf deiner Festplatte\r\n4. Stell Ingame unter "Einstellungen -> Design" die Option "Bildpaket wählen" auf "(Selbstdefiniert)"\r\n5. Kopiere den Pfad zu deinem Ordner in das nebenstehende Eingabefeld (am schluss vom Pfad muss z.B. "/Discovery" oder "/classic_black" stehen\r\n6. Gib im Eingabefeld "Dateiendung" das Format der Bilder an (gif, jpg, png)\r\n7. Speicher mit einem klick auf "Übernehmen"\r\n\r\nSollte es trotz allem nicht gelingen, liegt es wahrscheinlich an deinem Browser. Eventuell kannst du noch was mit den Einstellungen des Browsers machen!\r\n\r\n[b]Imagepack-Fix für Firefox[/b]\r\nUnter Firefox wird das Laden von [B]lokalen Bildern[/B] grundsätzlich verhindert (wenn ihr das Bildpaket auf einen Webspace uploadet funktioniert alles wie gewünscht, bitte ignoriert dann den folgenden Text), siehe ([URL]http://www.firefox-browser.de/wiki/Lokale_Bilder[/URL]). \r\n\r\nBei einer [B]alten [/B]Firefox/Mozilla-Version hilft folgender Fix: \r\n\r\n1.) Bildpaket herunterladen und in einen Ordner entpacken (zB C:/Discovery)\r\n2.) Unter Einstellungen->Designs den Pfad zum Bildpaket eingeben, und zwar folgendermassen: [B]file://C:/Discovery/[/B]\r\n3.) Ein neues Fenster/einen neuen Tab öffnen und about:config (ohne Leerzeichen dazwischen) eingeben, ENTER\r\n4.) security.checkloaduri in der Filterzeile eingeben, den Wert der Eintrages auf [B]false [/B] ändern.\r\n\r\nHilf dies nichts oder hast du eine [B]neue Firefox Version[/B] (z.B. 2.0) so mache folgendes:\r\n\r\n5.) Geh in deinen Firefox-Profil-Ordner, dieser ist meinstens unter C:\\Dokumente und Einstellungen\\[I]WindowsUserName[/I]\\Lokale Einstellungen\\Anwendungsdaten\\Mozilla\\Firefox\\Profiles\\[I]ZufälligerName[/I]\r\n6.) Dort mit dem Texteditor eine Datei [B]user.js[/B] mit folgendem Inhalt erstellen:\r\n\r\n[color yellow]user_pref("capability.policy.policynames", "localfilelinks");\r\nuser_pref("capability.policy.localfilelinks.sites", "http://www.etoa.ch http://88.198.37.135");\r\nuser_pref("capability.policy.localfilelinks.checkloaduri.enabled", "allAccess");[/color]\r\n7.) Firefox neu starten und es sollte die Bilder anzeigen', '', '', '', 0, '', '', 20, 0, 0, 0),
(148, 0, 'Dies kann passieren wenn du einen falschen oder gar keinen Grafikcode beim Login eingegeben hast. Das Eingeben dieses vierstelligen Grafikcodes ist notwendig, um das automatisierte Einloggen durch Bot-Programme zu erschweren.', '', '', '', 0, '', '', 21, 0, 0, 0),
(149, 0, 'Dese Fehlermeldung erscheint wenn deine Spielsitzung beendet wurde. Dies kann passieren wenn du dich an einem anderen PC einloggst obwohl du noch mit diesem online warst! In seltenen Fällen kann auch ein Administrator deine Session beendet haben, dies geschieht jedoch nur in speziellen Fällen, z.B. bei Serverproblemen oder einem Betrugsverdacht.', '', '', '', 0, '', '', 22, 0, 0, 0),
(150, 0, 'Wenn du im Spiel eine gewisse Zeit lang online bist ohne dass du etwas machst wirst du nach einiger Zeit automatisch ausgeloggt. Dies ist nötig um Missbräuche zu verhindern.', '', '', '', 0, '', '', 23, 0, 0, 0),
(151, 0, 'Es ist nicht erlaubt, dass Spiel in mehreren Fenstern gleichzeitig zu betreiben, da damit in seltenen Fällen Schwachstellen ausgenützt und somit andere Spieler benachteiligt werden können. Die einzigen Ausnahmen bildet die Hilfe und der Notizblock, welche per Einstellung in einem separaten Fenster geoeffnet werden koennen.', '', '', '', 0, '', '', 24, 0, 0, 0),
(152, 0, '42 \r\n\r\n[url http://de.wikipedia.org/wiki/42_(Antwort)]Erklärung dazu[/url]', '', '', '', 0, '', '', 26, 1, 3, 0),
(153, 0, 'Invasieren kann man nur Planeten eines Spielers, der nicht im Anfängerschutz ist. Ebenfalls benötigt man dazu ein Schiff, dass invasieren kann. Hat man nun den Auftrag zur Invasion gegeben, kann immer noch etwas fehlschlagen. Zuerst muss der Kampf auf dem zu invasierenden Planeten gewonnen werden. Anschliessend versucht das Invasionssschiff den Planeten zu übernehmen, dies klappt jedoch nicht immer, da hier ein Zufallsfaktor mitspielt. Sollte die Invasion beim ersten Mal nicht klappen so muss man halt mehrere Versuche starten.', '', '', '', 0, '', '', 27, 0, 0, 0),
(154, 1178205771, 'Den persönlichen Avatar und die Signatur für das Allianzforum kannst du ingame unter "Einstellungen -> Allgemein" einstellen.\r\nWenn du in deinem Beitrag ein Bild einfügen willst, so kannst du dies folgendermassen tun:\r\n- dein Bild im Internet hochladen (Bsp. Imageshack odr so)\r\n- URL-Adresse des Bildes kopieren\r\n- im Beitrag den Code [color=red] [ img ] URL-Adresse [ /img] [/color] (ohne Leerstellen!) eintippen\r\n- Beitrag absenden', '', '', '', 0, '', '', 34, 0, 0, 1178205771),
(155, 1178222614, 'Es ist nicht festgelegt, wie lange eine Runde dauert. Als Faustregel gilt ca. 1/2 bis 1 Jahr.', '', '', '', 0, '', '', 35, 0, 0, 1178222614),
(156, 1178373678, 'Wenn du jemanden gesittet hast und die Zeit dann abgelaufen ist kommt diese Fehlermeldung. Bitte dann den Spieler, den du sittest, den Modus wieder zu aktivieren, falls du noch weiter auf den Account aufpassen sollst.\r\nWenn diese Fehlermeldung kommt, wenn du dich in DEINEN Account einloggen willst, melde dies bitte direkt einem Admin/Entwickler. Schildere bitte den genauen Hergang des Fehlers und erwähne, welchen Browser und welche Version du benutzt (Bsp. Mozilla Firefox 2.0).', '', '', '', 0, '', '', 36, 0, 0, 1178373678),
(157, 0, 'Es gibt 2 Sorten von Mysticums; zum einen das Andromeda Mysticum. Dieses spezielle Schiff kann Erfahrungspunkte (EXP) sammeln, die du dann für spezielle Bonis verwenden kannst. Jeder Spieler besitzt dieses Schiff. Zum anderen gibt es auch noch rassenspezifische Mysticums. Diese können in Kämpfen ebenfalls EXP sammeln und haben Bonis, welche die Vorteile der jeweiligen Rassen noch unterstützen bzw. ausbauen. Durch welche Technologien und Gebäude du das Mysticum erhälst, musst du schon selber herausfinden!', '', '', '', 0, '', '', 37, 0, 0, 0),
(158, 0, 'Die Minbari Jäger sind zu 100% getarnt, wenn du sie alleine fliegen lässt. Dabei musst du aber nicht einen normalen Angriff starten, sondern einen Tarnangriff durchführen. Wenn du Minbari Jäger vom Markt kaufst, werden sie dir auch nicht angezeigt, wenn sie auf dem Weg zu dir sind, das sie (logischerweise) getarnt sind.\r\nWenn du beim Minbari Mysticum jedoch die Bonis auf Tarnung setzt, dann wird die ganze Flotte getarnt. Wenn du dabei einen Boni vom 50% Tarnung hast, ist deine Flotte auf der halbe Strecke getarnt. Wenn sie also 1 Stunde fliegt, wird dein Gegner erst nach einer eine halbe Stunde vor Angriff die Schiffe erkennen können.', '', '', '', 0, '', '', 38, 0, 0, 0),
(159, 1232541239, 'Das verschenken oder verleihen von Schiffen ist nicht möglich. Wer von einem anderen Spieler Schiffe haben will, muss diese via Marktplatz kaufen. Dabei müssen zwischen 70% und 150% der Baukosten bezahlt werden. Andere Wege Schiffe zu tauschen gibt es nicht. Auch werden von den Admins keine Spezialwünsche oder Sonderregelungen erfüllt.', '', '', '', 0, '', '', 39, 0, 0, 1232541239),
(160, 1178979039, 'Im moment besteht noch ein kleines Balanceproblem mit den Mysticums. Darum haben einige Rassenmysticums noch nicht sehr gut abgestimmte Bonis. Dies wird aber sicher noch behoben.', '', '', '', 0, '', '', 40, 0, 0, 1178979039),
(161, 1179155972, 'Es kann verschiedene Gründe geben, warum man sich nicht einloggen kann. Bitte beachtet immer die Fehlermeldungen die angezeigt werden (Falsches PW, falscher Bildcode, falscher Nick etc.) All diese Fehler werden in der FAQ genauer erklärt.\r\nEs kann aber auch sein, dass der Server kurzzeitig überlastet ist. Dann wartet bitte 10 Minuten (oder auch mehr) und versucht es dann erneut. Wenn es immer noch nicht geht, werft einen Blick in die News oder ins Forum, evt. hat ein Admin eine Erklärung hinterlassen.\r\nWenn eine andere Fehlermeldung kommt (z.B. MySQL-Fehler), dann meldet die bitte EINMAL im Forum (und nur einmal, es hilft nichts wenn jeder darunter schreibt: "Bei mir geht''s auch net!"). Ein Admin oder Entwickler wird sich dann darum kümmern.\r\nEs kann auch sei, dass gerade ein Entwickler neue Daten auf den Server lädt! Dann wartet ebenfalls ein paar Minuten und versucht es erneut! Auch in diesem Fall bitte wieder News und Forum lesen!\r\nWenn alles nichts hilft, bitte Mail mit der GENAUEN Fehlerbeschreibung an einen Admin oder die Spielleitung! Euch sollte dann geholfen werden.', '', '', '', 0, '', '', 41, 0, 0, 1179155972),
(162, 1179377420, 'Baue in der Raumschiffswerft ein Kolonialisierungsschiff. Wähle es dann im Hafen aus und schicke es zu einem unbesiedelten Planeten. Wähle in der Schiffsaktion "Kolonialisieren" aus und sende das Schiff aus. Das Kolo-Schiff wird dann auf dem neuen Planeten zerlegt und zum Bau der Planetenbasis verwendet. Dadurch verschwindet das Schiff aus dem Hafen.', '', '', '', 0, '', '', 42, 0, 0, 1179377420),
(163, 0, 'Wenn du auf einem deiner Planeten nichts mehr bauen kannst, musst du entweder Verteidigung recyclen oder die Orbitalplattform ausbauen. Es ist im Moment immer noch möglich, die Felderzahl zu überziehen! In diesem Fall kannst du auch die Orbitalplattform nicht mehr ausbauen. Recycle in dem Fall so viel Def, bis du wieder auf Null bist mit den Feldern und baue dann die Orbitalplattform aus.', '', '', '', 0, '', '', 43, 0, 0, 0),
(164, 1180548337, 'Jeder Planet, jede Sonne und jede Rasse hat ihre speziellen Vor- und Nachteile. Aufgrund dieser Vor- und Nachteile hat jeder Spieler auf seinem Planeten bestimmte Bonis. Dies sind erhöhte/verminderte Produktionsraten, schnellere/langsamere Schiffe oder bessere/schlechtere Bau- und Forschungszeiten. Durch die Kombination von Rasse, Planten- und Sonnentyp kann man sich eine ausgewogene Produktion schaffen. Wer beispielsweise einen hohen Rassenboni auf Tritium hat, sollte nicht noch einen Planeten suchen, der einen Tritiumboni hat, sodern versuchen, die anderen (negativen oder schlechteren) Rassenboni zu kompensieren. Durch diesen Boni kann sich jeder eine eigene Strategie festlegen, denn wer beispielsweise schnelle Schiffe hat aber nur negative Produktionsboni, sollte wohl eher auf Angriffe (Raids) aus sein als auf Bauen und Produzieren...\r\nDie jeweiligen Bonis werden unter Wirtschaft tabellarisch aufgelistet und farblich hervorgehoben.', '', '', '', 0, '', '', 46, 0, 0, 1180548337),
(165, 1180456544, 'Wenn du dein Passwort vergessen hast oder es aus sonst einem Grund nicht mehr weisst, dann kannst du dir per Mail ein neues zusenden lassen. Klicke dazu auf der Startseite auf den Button "PW-Request". Das System generiert dir dann ein neues Passwort, mit dem du dich wieder anmelden kannst.\r\nWICHTIG: Passwörter werden im System nicht gespeichert! Wenn du ein PW-Request durchführst, bekommst du IMMER ein neues Passwort.\r\nWenn du das neue Passwort nicht sofort bekommst, durchsuche auch den Spamordner, denn manchmal werden Mails von EtoA als Spam klassifiziert.\r\nNach dem Erhalten des neuen PW''s solltest du dir unbedingt ein neues zulegen und es Ingame in den Einstellungen ändern gehen.\r\nWenn etwas wirklich nicht geht mit dem PW-Request, melde dich direkt per Mail an mail@etoa.ch', '', '', '', 0, '', '', 45, 0, 0, 1180456544),
(166, 0, 'Wenn bei dir auf der Startseite Ingame eine fremde aber friedliche Flotte angezeigt wird, du diese aber nicht sehen kannst, wenn du sie anklickst, brauchst du nicht zu verzweifeln. Zu 99% hast du vom Markt Minbari Jäger gekauft, die nun auf dem Weg zu dir sind. Wenn diese Schiffe vom Markt zu dir fliegen sind sie, auf Grund ihrer rassenspezifischen Eigenschaft, getarnt. Du siehst zwar auf der anzeige, dass eine Handelsflotte zu dir unterwegs ist, siehst aber die Schiffe nicht.\r\nFür den Fall, dass du KEINE Minbari Jäger gekauft hast, es ist aber trotzdem eine Geisterflotte zu dir unterwegs, dann melde dies bitte einem Ingame-Admin . Dieser wird sich das ganze dann ansehen.', '', '', '', 0, '', '', 47, 0, 0, 0),
(167, 1180857436, 'Der Handel von Bewohnern ist grundsätzlich erlaubt. Es gelten aber die selben Regeln wie beim Rohstoffhandel: Innert 12h muss eine Gegenleistung erbracht werden. Der "Wechselkurs" für ein Bewohner ist in etwa 1 Bewohner = 1t Ressis (egal von was). Es darf auch hier nicht gepuscht und Bewohner überteuer verkaft werden!', '', '', '', 0, '', '', 49, 0, 0, 1180857436),
(168, 1182881639, 'Das Sonnensystem kann nur einmal benannt werden. Der Spieler, der als erstes einen Planeten in diesem Sonnensystem besiedelt hat die Chance diesem System einen Namen zu geben. Ist der Stern mal benannt, kann dies nicht mehr geändert werden.', '', '', '', 0, '', '', 54, 0, 0, 1182881639),
(169, 1182449096, 'Das geht grundsätzlich nicht, ausser du löst die Kolonie auf, dann sind die Ressourcen weg. Aber es ist meistens sinnvoller damit eine Flotte zu bauen ;)', '', '', '', 0, '', '', 53, 0, 0, 1182449096),
(170, 1183473816, 'Diese Frage lässt sich nicht allgemein beantworten. Es hängt von deiner Spiotech sowie von der des Gegners ab. Wenn du deine höher hast als der Gegner, dann ist auch deine Chance grösser als wenn sie gleich hoch sind. Eine reine Prozentzahl lässt sich aber nicht benennen, es sind jedoch in allen Fällen nur wenige Prozent (z.B. beide Spiotech auf gleichem Level; von 100 Angriffen sind nur etwa 1-2 erfolreich).\r\nWICHTIG: Auch Spioangriffe gelten als Angriffe; darum ist auch hier die Angriffsregelung zu beachten!!!', '', '', '', 0, '', '', 55, 0, 0, 1183473816),
(171, 1183473988, 'Laut den Entwicklern sollte es eigentlich nicht möglich sein! Falls doch einmal die Basis zerstört werden sollte (und dadurch der Bauhof gesperrt ist) meldet dies bitte einem Admin der entsprechenden Runde.', '', '', '', 0, '', '', 56, 0, 0, 1183473988),
(172, 0, 'Ein Screenshot ist ein (digitaler) Ausdruck deines Bildschirms, ein Bild von dem, was jetzt gerade auf meinem Computer zu sehen ist.\r\nUm einen Screenshot zu erstellen (z.B. um einem Entwickler einen Fehler zu melden) musst du die "Print Screen" Taste auf deiner Tastatur drücken.\r\n\r\n[img]../images/faq/printscreen.jpg[/img].\r\n\r\nUm diesen Screenshot dann in ein "richtiges" Bild zu verwandeln, öffne ein Bildbearbeitungsprogramm wie z.B. Microsoft Paint, Adobe Photoshop oder ähnliche. Dort öffnest du eine neue leere Vorlage und klickst auf "Einfügen". Nun kannst du das Bild bei Bedarf noch bearbeiten (Grösse zurechtschneiden, Koords übermalen und ähnliches).\r\n\r\nSpeichere das Bild dann im JPG-Format auf deinen PC und lade es anschliessend auf einen Internetserver wie z.B. [url http://img1.imageshack.us]Imageshack[/url] hoch.\r\n\r\nUm das Bild nun im Forum zu posten, fügst du via BB-Code den Link zum Bild oder direkt das Bild selber (wenn es nicht zu gross ist) ein. So kann sich nun jeder deinen Schreenshot selbst ansehen!', '', '', '', 0, '', '', 57, 0, 0, 0),
(173, 1184491217, 'Natürlich können in einer Familie alle EtoA spielen wenn sie wollen. Es ist aber einfach nötig, dass sich dann alle User in der Multikontrolle (zu finden unter Einstellungen ingame) eintragen müssen; wichtig ist dabei, dass die jeweiligen Beziehungen angegeben werden (z.B. Bruder, Mutter etc.). Dies gilt natürlich nur, wenn in einer Familie alles über die selbe IP läuft (und nicht nur über den selben Computer)!!\r\nWer in der Multikontrolle eingetragen ist, kann untereinander nicht handeln! Nicht einschreiben in der Multikontrolle oder ein Handel unter Familienmitgliedern, welche eingeschrieben sind, wird von den Admins bestraft und die betroffenen User gesperrt!', '', '', '', 0, '', '', 58, 0, 0, 1184491217),
(174, 1195332020, 'Das so genannte Real Life (engl.: „wirkliches Leben“, gelegentlich auch meat life, etwa: „fleischliches Leben“, kurz: RL) verwendet man in der Computerszene als Begriff für das Leben eines Menschen außerhalb seiner Tätigkeit in einer Gruppe gleichgesinnter Computerinteressierter. (Aus: [url=http://de.wikipedia.org/wiki/Real_Life_%28Netzkultur%29]Wikipedia[/url])\r\nWenn also jemand wissen will, was du in deinem RL machst, dann heisst das nichts anderes als dass er wissen will, was du machst, wenn du nicht EtoA spielst :) (falls es in deinem Leben etwas anderes gibt ausser EtoA natuerlich :P)', '', '', '', 0, '', '', 71, 0, 0, 1195332020),
(175, 1185302198, 'Wenn sich ein Spieler neu anmeldet, sich aber noch nie eingeloggt hat, hat er automatisch den Sektor 0/0. Erst nach dem ersten Login wird dann der Sektor korrekt angezeigt. Es wird nämlich erst beim Login ein Hauptplanet (HP) zugeordnet, also kann vorher auch noch kein Sektor vorhanden sein!', '', '', '', 0, '', '', 67, 0, 0, 1185302198),
(176, 1186226704, 'Wenn bei dir bein Login die folgende Fehlermeldung auftritt;\r\n"Fehler: Unbekannter Fehler (sitting). Bitte den Entwickler kontaktieren!",\r\ndann wende dich an einen Admin. Er kann dir dann weiterhelfen.', '', '', '', 0, '', '', 68, 0, 0, 1186226704),
(177, 1193323745, 'Um einen Planeten aufzulösen müssen folgende Bedingungen erfüllt sein:\r\n- Es darf nicht dein Hauptplanet sein\r\n- Es darf nichts im Bau sein und nichts erforscht werden\r\n- Es dürfen keine Flotten vom oder zum Planeten unterwegs sein (gilt auch für fremde Flotten)', '', '', '', 0, '', '', 85, 0, 0, 1193323745),
(178, 1193341102, '[b]Spielpasswort:[/b]\r\nGehe auf Einstellungen -> Passwort. Dort musst du dein altes Passwort und danach zweimal das neue Passwort eingeben. Klicke auf Übernehmen um das neue Passwort zu speichern.\r\n\r\n[b]Forum:[/b]\r\nKlicke auf Profil, dann auf Passwort ändern ([url=http://etoa.dysign.ch/forum/usercp.php?action=password_change]Direktlink[/url]). Nun musst du dein altes Passwort und zweimal dein neues Passwort eingeben. Danach bestätigen, dass Passwort wird sofort geänder.\r\n\r\n[b]Chat:[/b]\r\nAuthentifiziere dich im Chat, entweder direkt über das Login-Formular oder mit\r\n[i]/authserv auth nickname passwort[/i]\r\nUm dein Passwort zu ändern musst du folgenden Befehl eingeben:\r\n[i]/authserv pass altespasswort neuespasswort[/i]', '', '', '', 0, '', '', 83, 0, 0, 1193341102),
(179, 1191060045, 'In den Allianztexten sind alle in der Hilfe aufgefuehrten bb-Codes erlaubt. Eine Liste findest du [url=http://round2.etoa.net/show.php?page=help&site=textformat]hier[/url].\r\nDie Verwendung von HTML und PHP ist nicht erlaubt und funktioniert auch nicht!', '', '', '', 0, '', '', 75, 0, 0, 1191060045),
(180, 1190563713, 'Die Raketen bilden ein eigenes Verteidigungs- und Angriffssystem. Die genaue Funktionsweise und der gebrauch ist im Raketensilo und in der [url=http://round2.etoa.net/?page=help&site=missiles]InGameHilfe erlaeutert[/url].', '', '', '', 0, '', '', 73, 0, 0, 1190563713),
(181, 1191349941, 'Die Piloten sind Teile der normalen Bevoelkerung. Wenn du also alle deine Bevoelkerung eingesetzt hast, hast du keine Piloten mehr uebrig. Wichtig ist also, dass du immer ein paar ueberzaehlige Bewohner hast. Die Bewohner vermehren sich automatisch (nach dem Wert in der Wirtschaft) solange dein Wohnmodul genug Platz hat.', '', '', '', 0, '', '', 76, 0, 0, 1191349941),
(182, 1193303299, 'Nein, wir haben einen fest eingebauten Chat. Er ist ingame über den Button Chat zu erreichen.', '', '', '', 0, '', '', 84, 0, 0, 1193303299),
(183, 1192810670, 'In einem Kampf wird immer zuerst auf den Schild geschossen. Diese muessen zuerst zerstoert werden, damit das Schiff selbst zerstoert werden kann. Sind die Schilder weg, wird die Struktur angegriffen. Erst jetzt kann das Schiff zerstoert werden.', '', '', '', 0, '', '', 81, 0, 0, 1192810670),
(184, 1193594915, 'Die wohl am haeuffigsten gestellte Frage im EtoA hier nun ein fuer alle mal Beantwortet:\r\nONEFIGHT ist ein englisches Wort. ONE steht dabei fuer EINS und FIGHT heisst KAMPF. Es ist also uebersetzt ein EINKAMPF-Schiff. Wie der Name also sagt, ist es NUR fuer EINEN Kampf zu gebrauchen!! Ones gehen IMMER kaputt, egal ob du den Kampf gewinnst oder nicht! Setze sie darum klug ein, denn sonst sind deine Ressis schnell verloren :)', '', '', '', 0, '', '', 86, 0, 0, 1193594915),
(185, 1193661851, 'Ein Besiedlungsschiff hat nur so viele Bewohner, wie es auch Piloten hat. Es ist nicht moeglich, zusaetzliche Bewohner mitzuschicken. Wenn du auf einem neuen Planeten mehr Bevoelkerung willst, musst du diese mit einem geeigneten Transportschiff hinschicken.', '', '', '', 0, '', '', 87, 0, 0, 1193661851),
(186, 1194371655, 'Grundsätzlich gibt es kein Maximum (respektive es ist bei 999). Einige Gebäude und Technologien haben jedoch ein Maximum, dies wird in der Hilfe im Spiel bei den Details zur Technologie oder zum Gebäude angezeigt.', '', '', '', 0, '', '', 88, 0, 0, 1194371655),
(187, 1195423649, 'Um Raketen zu bauen, musst du die dafuer noetigen Technologien erforschen wie auch das Raketensilo gebaut haben. Dann bist du im stande, auch die Raketen zu deiner Verteidigung und zum Angriff zu nutzen.', '', '', '', 0, '', '', 93, 0, 0, 1195423649),
(188, 1195429535, 'Du kannst ein sog. Profilbild heraufladen. Gehe dazu auf Einstellungen -> Profil. Bei User-Bild kannst du auf Durchsuchen klicken und ein Bild von deinem Computer aussuchen. \r\nEs gelten folgende Regeln für das Bild:\r\n- Maximal 1280*1024 Pixel, Bilder grösser als 640*480 werden automatisch verkleinert. \r\n- Format: GIF, JPG oder PNG. \r\n- Grösse: Max 2 MByte\r\nKlicke danach auf Übernehmen und warte einen Augenblick, bis die Bestätigungsmeldung kommt.', '', '', '', 0, '', '', 94, 0, 0, 1195429535),
(189, 1195937111, 'Wenn ein Spieler inaktiv wird, ist er von allen angreiffbar, auch wenn eigentlich Noob-/Anfaengerschutz aktiv ist. Der Inaktivenstatus liegt dann vor, wenn ein Spieler laenger als 7 Tage nicht in seinem Account eingeloggt war.', '', '', '', 0, '', '', 95, 0, 0, 1195937111),
(190, 1196780484, 'Wenn du dich mit einem anderen Spieler verbuenden willst, kannst du das tun. Du musst das niemandem sagen oder irgendwo eintragen. Viele Spieler haben aber ihre Verbuendeten irgenwo in ihrem Profil vermerkt.\r\nWenn du deine Allianz mit einer anderen verbuenden willst, dann kannst du dies in den Allianzeinstellungen tun (wenn du die erforderlichen Rechte dazu hast). Im Rathaus werden die neusten Allianzbuendnisse angezeigt, es waere also schoen, wenn du eine etwas einfallsreiche Buendnissanfrage schreiben wuerdest.', '', '', '', 0, '', '', 101, 0, 0, 1196780484),
(192, 1196890369, 'Wenn du einen Admin kontaktieren willst, kannst du dies per [url=http://etoa.dysign.ch/forum/index.php]Forum[/url] tun. Am einfachsten sendest du eine PM (Privatnachricht) an einen der Admins. Oder du sendest eine Mail an [email]mail@etoa.ch[/email], dann kann die Nachricht an den entsprechenden Admin weitergeleitet werden.', '', '', '', 0, '', '', 102, 0, 0, 1196890369),
(193, 1197149755, 'Wenn du mehr als 7 Tagen nicht in deinem Account warst, wirst du als inaktiv angezeigt. Nach 21 Tagen wird dein Account dann geloescht! Du wirst im Vornherein aber per Mail informiert, dass dein  Account geloescht wird. So kannst du den Account vor der Loeschung bewahren, wenn du mal vergessen hast, dich einzuloggen :)', '', '', '', 0, '', '', 103, 0, 0, 1197149755),
(194, 1198105922, 'Im Marktplatz bekommst du nie 100% deines Angebotes zurueck. Das bedeutet also logischerweise, dass du nie ein ganzes Schiff zurueck bekommst. Wenn du also ein Schiff in den MP stellst und es dann zurueckziehst, verlierst du es. Du bekommst auch keine Ressis zuerueck!!!', '', '', '', 0, '', '', 104, 0, 0, 1198105922),
(195, 1198255193, 'Wenn durch eine Bombaridierung ein Gebaeude herabgesetzt wir und dadurch Voraussetzungen fuer andere Gebaeude etc. nicht mehr erfuellt sind, passiert folgendes:\r\n- andere Gebaeude: das Gebaeude, kann nicht mehr weiter ausgebaut werden, bis die Voraussetzungen wieder erfuellt sind. Minen produzieren aber weiter, auch wenn die noetigen Voraussetzungen nicht erfuellt sind.\r\n- Schiffe: sie koennen nicht mehr gebaut werden, bis die Voraussetzungen wieder erfuellt sind, sind aber immer noch startbar.\r\n- Technologien: sie koennen nicht mehr weiter erfoschrt werden, bis die Voraussetzungen wieder erfuellt sind!\r\n- Verteidigungsanlagen: sie koennen nicht mehr gebaut werden, bis die Voraussetzungen wieder erfuellt sind. Sie Verteidigen aber weiterhin deinen Planeten.', '', '', '', 0, '', '', 105, 0, 0, 1198255193),
(196, 1198835091, 'Melde dich mit dem Formular in der InGame Hilfe oder im Forum bzw. im neuen Fehlermelde Tool welches du [url=http://dev.etoa.net:8000/game]hier[/url] findest.', '', '', '', 0, '', '', 108, 0, 0, 1198835091),
(197, 1233572443, 'Für eine gewisse Anzahl an erspielten Punkten in der betreffenden Kategorie gibt es Extra-Punkte. Fiktives Beispiel: 1000 Gebäudepunkte = 1 Spezial-Gebäudepunkt. (Die normalen Gebäudepunkte werden anhand der verbauten Ressourcen berechnet.)\r\nAnhand dieser Extra-Punkte werden dann die dazugehörigen Titel vergeben.', '', '', '', 0, '', '', 155, 0, 0, 1233572443),
(198, 1231509241, 'Ueberhaup nicht. Die Wurmlochforschung ermoeglicht nur, dass die Flotte durch Wurmloecher fliegen kann. Sobald diese Technologie erforscht ist, kann deine Flotte Wurmloecher benutzen, um "Abkuerzungen" durch das Universum zu nehmen und so Zeit sparen. Wichtig ist aber, dass man immer bedenkt, dass Wurmloecher veraenderlich sind und du, wenn du Pech hast, am anderen Ender der Galaxie endest, als das du sein wolltest...\r\nDer Tritiumverbrauch der Schiffe ist auch weniger gering, da nur die Strecke vom Startplaneten bis Wurmlocheingang und Wurmlochausgang bis Endplaneten Tritium verbraucht!', '', '', '', 0, '', '', 156, 0, 0, 1231509241),
(199, 1231509323, 'Nein, der Urlaubsmodus ist unbeschraenkt. Jeder Spieler kann und darf so lange im Urlaub bleiben, wie er will. Der Account wird erst auf Wunsch des Spielers geloescht.', '', '', '', 0, '', '', 157, 0, 0, 1231509323),
(200, 1231492496, '1) Fuelle deinen Namen (am besten deinen Etoa Foren- oder Ingamenick) in das entsprechende Feld ein\r\n2) Gib eine GUELTIGE E-Mailadresse an. Diese wird weder veroeffentlicht noch gespeichert. Sie dient lediglich dazu, dass du bei allfaelligen Rueckfragen einfach und schnell kontaktiert werden kannst. Ausserdem bekommst du ein E-Mail, sobald deine Frage beantwortet und/oder veraendert wurde.\r\n3) Fuege deine Frage moeglichst kurz und buendig ein. Achte hier bitte auf eine anstaendige Rechtschreibung, so dass deine Frage auch gut und richtig verstanden wird!', '', '', '', 0, '', '', 158, 0, 0, 1231492496),
(201, 1231510294, 'Die Flotte fuehrt ihren Auftrag aus und kehrt dann zu deinem Hauptplaneten zurueck.\r\nFalls dies nicht der Fall sein sollte, melde dich bitte bei einem Admin.', '', '', '', 0, '', '', 159, 0, 0, 1231510294),
(202, 1231863223, 'Die Regenatechnologie ermoeglicht dir eine Heilung von Schiffen, nicht aber von Gebaeuden oder Verteidigungsanlagen.', '', '', '', 0, '', '', 160, 0, 0, 1231863223),
(203, 1237137592, 'Diese Frage wird in der Ingame-Hilfe ausführlich beantwortet. Hier der Link dazu.\r\nhttp://round5.etoa.ch/show.php?page=help&site=specialists', '', '', '', 0, '', '', 162, 0, 0, 1237137592),
(204, 1237214234, 'Es wird so gehandhabt, dass ein gemeinsames Login am Vormittag während Schultagen nicht automatisch zur Sperre führt. Was nicht heisst, dass bei Unstimmigkeiten nicht doch gesperrt wird. Die ist ein Entgegenkommen der Spielleitung.', '', '', '', 0, '', '', 163, 0, 0, 1237214234),
(205, 1237729830, 'Nein. Jegliches verschenken von Rohstoffen, Schiffen oder Planeten ist grundsaetzlich nicht moeglich. Falls du aber doch eine Moeglichkeit finden solltest, dies zu tun, dann handelt es sich um einen Bug. jegliches ausnutzen von Bugs oder anderen Fehlern im Spiel wird mit Sperren bestraft, wobei deren Laenge in den Regeln festgelegt ist.', '', '', '', 0, '', '', 165, 0, 0, 1237729830),
(206, 1239560446, 'Ja! Pro 1000 verbaute Rohstoffe bekommst du einen Punkt. Dies gilt fuer deine Gebaeude, Schiffe, Verteidigungsanlagen sowie auch Forschungen. Wenn du irgend etwas davon verlierst, dann werden auch die Punkte wieder abgezogen. Darum wirst du wohl sehr viele Punkte verlieren, wenn du eine Kolonie aufhebst.', '', '', '', 0, '', '', 171, 0, 0, 1239560446),
(207, 1239699318, 'Dies haengt davon ab, ob es eine Sperre mit oder ohne Angriffsschutz ist. Dies haeng wiederum vom Grund der Sperre ab. Falls du es genau wissen moechtest, melde dich beim Admin, der dich gesperrt hat und frage nach.', '', '', '', 0, '', '', 172, 0, 0, 1239699318),
(208, 1238524329, 'Das Mysticum ist eines der wenigen Objekte in EtoA, bei denen der User selber herausfinden muss, was es macht, wozu es gut ist und welche Voraussetzungen es braucht. Viel Spass beim Entdecken =).', '', '', '', 0, '', '', 169, 0, 0, 1238524329),
(209, 1240132926, 'Das Bauen von mobilen Verteidigungsanlagen und den dazugehoereigen Trägerschiffen ist der Rasse der Serrakin vorbehalten. Diese koennen sie jeweils in der Waffenfabrik und in der Schiffswerft gebaut werden.', '', '', '', 0, '', '', 174, 0, 0, 1240132926),
(211, 1246883417, 'Der AURIGA Explorer ist das einzige Schiff, welches die Option "Erkunden" zur Verfuegung hat.', '', '', '', 0, '', '', 187, 0, 0, 1246883417),
(212, 1240564017, 'Das Kryptocenter ist eines der wenigen Objekte in EtoA, bei denen der User selber herausfinden muss, was es macht, wozu es gut ist und welche Voraussetzungen es braucht. Viel Spass beim Entdecken =).', '', '', '', 0, '', '', 178, 0, 0, 1240564017),
(213, 1241011664, 'Wenn du ein geeigentes Schiff auf ein Asteroidenfeld schickst, um Rohstoffe abzusammeln, koennen nur Titan, Silizium und PVC gesammelt werden.', '', '', '', 0, '', '', 179, 0, 0, 1241011664),
(214, 1242251244, 'Nein! Sobald das maximum von 15 Planeten erreicht ist, sind Besiedlung und auch Invasion nicht mehr moeglich. Beide Aktionen werden in diesem Fall vom System verboten.', '', '', '', 0, '', '', 182, 0, 0, 1242251244),
(215, 1242565621, 'Unter ''Einstellungen->Sonstigen->Account loeschen'' kann der Account geloscht werden.', '', '', '', 0, '', '', 183, 0, 0, 1242565621),
(216, 1243942829, 'Es gibt verschiedene Moeglichkeiten, den eigenen Punktestand ausfindig zu machen:\r\n1) In der Spielstatistik.\r\n2) In deinem oeffentlichen Profil.\r\n3) Im Dark und Classic Design ganz unten an der Seite, wo auch Zeit und der Nick des Momentan eingeloggter User zu sehen sind.', '', '', '', 0, '', '', 184, 0, 0, 1243942829),
(218, 1243944604, 'Der Föderationsexplorer ist ein spezielles Schiff, welches den Spielern in Runde 2 gegen Ende des Jahres 2008 von der Föderation Andromeda geschenkt wurde. Es ist ein spezielles Schiff, welches weder gebaut oder gehandelt werden kann. Benutze es weise, denn ist es einmal weg, so bleibt es auch weg...', '', '', '', 0, '', '', 185, 0, 0, 1243944604),
(219, 1249754148, 'Nein! Jeglicher Handel unter eingetragenen Multis ist NICHT erlaubt. Bei nichtbeachtung dieser Regel werden die betreffenden Spieler gesperrt!', '', '', '', 0, '', '', 194, 0, 0, 1249754148),
